func (tls *TransferLeafSelect) Aggregate(fns ...AggregateFunc) *TransferLeafSelect {
	tls.fns = append(tls.fns, fns...)
	return tls
}

// Scan applies the selector query and scans the result into the given value.
func (tls *TransferLeafSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, tls.ctx, ent.OpQuerySelect)
	if err := tls.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*TransferLeafQuery, *TransferLeafSelect](ctx, tls.TransferLeafQuery, tls, tls.inters, v)
}

func (tls *TransferLeafSelect) sqlScan(ctx context.Context, root *TransferLeafQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(tls.fns))
	for _, fn := range tls.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*tls.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := tls.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
